import { StatusBar } from 'expo-status-bar';
import React,{useState} from 'react';
import { StyleSheet, Button,Text, View } from 'react-native';
import QuizBody from './components/QuizBody';

export default function App() {

  const [questions,setQuestions] = useState([]);
  const [category,setCategory] = useState(null);
  const [difficulty,setDifficulty] = useState(null);
  const [currentPage,setCurrentPage] = useState('category-select');



  async function fetchQuizzes(){ 
    const result = await fetch('https://opentdb.com/api.php?amount=10');
    const json = await result.json();
    console.log(json.results);
    setQuestions(json.results);
  };

  function changeCategory(catId){
    setCategory(catId);
    setCurrentPage('difficulty-select');

  }

 if (currentPage == 'category-select'){

  return (
    <View style={styles.container}>
      <Text>Welcome to quiz</Text>
      <Text>Select Category</Text>
    <View>

    <Button
        onPress={()=>changeCategory(21)}
        title="Sports"
        style={styles.menuButton}
        >
    </Button> 

    <Button
        onPress={()=>changeCategory(19)}
        title="General Knowledge">
       style={styles.menuButton}
    </Button>

      <StatusBar style="auto" />
    </View>
    </View>

  );
  }

  else if (currentPage == 'difficulty-select') {
    return (
      <View style={styles.container}>
        <Text>Select difficulty</Text>
        <Button title="easy"  />
        <Button title="medium" />
        <Button title="hard" />
      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // backgroundColor: '#ed3',
    backgroundColor: '#4a4a46',
    alignItems: 'center',
    justifyContent: 'center',
  },
  menuButton: {
    padding:'1%',
  },
});
