import React,{useState} from 'react';
import { Button, View ,Text} from 'react-native';

export default function QuizBody(props){
    const [currentQuestion,setCurrentQuestion] = useState(0);
    const question = props.questions[currentQuestion];
    let answers = [];
    // console.log(Object.keys(question.answers).length);

    function renderAnswerRow(){
    Object.keys(question.answers).forEach(function(key, index) {
     answers.push(<View style={{
            width:'80%',
            left:'10%',
            padding: '5%'
        }}>
            <Button title={this[key]}

           color="#250c6f"
            />
        </View>)
      }, question.answers);

      return answers;
    }

    return (
        <View style={{
            height:'100%',
            width:'100%',
            backgroundColor:'#000',
        }}>
            <View style={{
                 height:'20%',
                //  width:'50%',
                //  backgroundColor:'#067',
                 color:"#fff",
                 alignItems: 'center',
                 justifyContent: 'center',
            }}

            >
                <Text style={{
                    color:'white',

                }}>{question.question}</Text>
            </View>


            <View style={{
                    height:'45%',
                    //  width:'50%',
                     backgroundColor:'#024',
                     color:"#4a4",
                     margin: '4%',
                    //  border: '2px solid #fff',
            }}
            >


                {renderAnswerRow()}

            </View>


        </View>
    );
}
