import { StatusBar } from 'expo-status-bar';
import React,{useState} from 'react';
import { StyleSheet, Button,Text, View } from 'react-native';
import QuizBody from './components/QuizBody';

export default function App() {

  const [questions,setQuestions] = useState([]);
  const [category,setCategory] = useState(null);
  const [difficulty,setDifficulty] = useState(null);
  const [currentPage,setCurrentPage] = useState('cat-selection');



  async function fetchQuizzes(){ 
    const result = await fetch('https://opentdb.com/api.php?amount=10');
    const json = await result.json();
    console.log(json.results);
    setQuestions(json.results);
  };

  function changeCategory(catId){
    setCategory(catId);

  }

 if (currentPage == 'cat-selection'){

  return (
    <View style={styles.container}>
      <Text>Welcome to quiz</Text>
      <Text>Select Category</Text>
    <View>

    <Button
        onPress={()=>changeCategory(21)}
        title="Sports">
    </Button> 

    <Button
        onPress={()=>changeCategory(19)}
        title="General Knowledge">
    </Button>

      <StatusBar style="auto" />
    </View>
    </View>

  );
  }  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // backgroundColor: '#ed3',
    backgroundColor: '#4a4a46',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
