import React,{useState} from 'react';
import { Button, View ,Text} from 'react-native';

export default function QuizBody(props){
    const [currentQuestion,setCurrentQuestion] = useState(0);
    const question = props.questions[currentQuestion];
    console.log(Object.keys(question.answers).length);
    // Object.keys(myArray).length

    for (let i=0;i<Object.keys(question.answers).length;i++){
        console.log(question.answers[i]);
    }

    return (
        <View style={{
            height:'100%',
            width:'100%',
            backgroundColor:'#000',
        }}>
            <View style={{
                 height:'20%',
                //  width:'50%',
                //  backgroundColor:'#067',
                 color:"#fff",
                 alignItems: 'center',
                 justifyContent: 'center',
            }}

            >
                <Text style={{
                    color:'white',

                }}>{question.question}</Text>
            </View>


            <View style={{
                    height:'45%',
                    //  width:'50%',
                     backgroundColor:'#024',
                     color:"#4a4",
                     margin: '4%',
                    //  border: '2px solid #fff',
            }}
            >
                <View style={{
                    width:'80%',
                    left:'10%',
                    top: '5%',
                    border: '1px solid #fff'
                }}>
                <Button title="answer1" 
                    color="#250c6f"
                />

                </View>

                <View style={{
                    width:'80%',
                    left:'10%',
                    top: '8%'
                }}>
                    <Button title="answer2"
                    color="#250c6f"
                    />
                </View>
            </View>


        </View>
    );
}
