import React,{useState,useEffect} from 'react';
import { Button, View ,Text} from 'react-native';
import AnswerOption from './AnswerOption';
export default function QuizBody(props){
    const [question,setQuestion] = useState(props.question);
    const [questionAnswered,setQuestionAnswered] = useState(false);
    const [correctlyAnswered,setCorrectlyAnswered] = useState(false);
    const [questionChanged,setQuestionChanged] = useState(false);
    let answers = [];
    let isCorrectAnswer = false;
    // console.log(Object.keys(question.answers).length);
    function renderAnswerRow(){
    Object.keys(question.answers).forEach(function(key, index) {
     if (this[key]){
         if (question['correct_answer']==key){
              isCorrectAnswer = true;
         }
     answers.push(<AnswerOption key={key} answerKey={key} answer={this[key]} correctAnswer={question['correct_answer']} 
                     setQuestionAnswered={setQuestionAnswered} isCorrectAnswer={isCorrectAnswer}
                     setCorrectlyAnswered={setCorrectlyAnswered} correctlyAnswered={correctlyAnswered}
                     questionAnswered={questionAnswered}
     />)}
         isCorrectAnswer = false;
      }, question.answers);

      return answers;
    }

    console.log(questionAnswered);

    function handleQuestionChange(){
        props.setCurrentQuestion(props.currentQuestion+1);
        setQuestionAnswered(false);
        setQuestionChanged(true);
    }

    useEffect( ()=>{
        if (questionChanged){
            setQuestionChanged(false);
        }
    })

    return (
        <View style={{
            height:'100%',
            width:'100%',
            backgroundColor:'#000',
        }}>
            <View style={{
                 height:'20%',
                //  width:'50%',
                //  backgroundColor:'#067',
                 color:"#fff",
                 alignItems: 'center',
                 justifyContent: 'center',
            }}

            >
                <Text style={{
                    color:'white',

                }}>{question.question}</Text>
            </View>


            <View style={{
                    height:'45%',
                    //  width:'50%',
                     backgroundColor:'#024',
                     color:"#4a4",
                     margin: '4%',
                    //  border: '2px solid #fff',
            }}
            >


                {renderAnswerRow()}

                <View style={{
                    // position:'relative',
                    color:'white',
                    top:'60%',
                    left:'75%'
                }}>
                    <Text style={{
                        color:'white'
                    }}
                    onPress={()=>{handleQuestionChange()}}
                    >
                        Next >>
                    </Text>
                </View>

            </View>


        </View>
    );
}
